import React, { Component } from 'react'


// This component renders the message depending upon the type of message. 

function Message(props) {

    var t = new Date();
    // For normal text message

    if (props.message.type === "text") {
        return (
            <div className="message clearfix">
                <div className={props.message.source}>
                    {props.message.message.split('\n').map((item, i) => { return <p key={i}>{item}</p> })}

                    <span className="tick"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="15" id="msg-dblcheck-ack" x="2063" y="2076"><path d="M15.01 3.316l-.478-.372a.365.365 0 0 0-.51.063L8.666 9.88a.32.32 0 0 1-.484.032l-.358-.325a.32.32 0 0 0-.484.032l-.378.48a.418.418 0 0 0 .036.54l1.32 1.267a.32.32 0 0 0 .484-.034l6.272-8.048a.366.366 0 0 0-.064-.512zm-4.1 0l-.478-.372a.365.365 0 0 0-.51.063L4.566 9.88a.32.32 0 0 1-.484.032L1.892 7.77a.366.366 0 0 0-.516.005l-.423.433a.364.364 0 0 0 .006.514l3.255 3.185a.32.32 0 0 0 .484-.033l6.272-8.048a.365.365 0 0 0-.063-.51z" fill="#4fc3f7" /></svg></span>
                    <span className="time">{t.getHours()}</span>
                </div>

            </div>
        )

    }

    // For PDF Messages

    if (props.message.type === "pdf") {
        return (
            <div className={props.message.source}>
                <div className="pdf">
                    <img className="pdf-icon" src="https://image.flaticon.com/icons/svg/179/179483.svg" alt="PDF-ICON"></img>
                    <div className="pdf-name">{props.message.message}</div>
                </div>

                <span className="tick"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="15" id="msg-dblcheck-ack" x="2063" y="2076"><path d="M15.01 3.316l-.478-.372a.365.365 0 0 0-.51.063L8.666 9.88a.32.32 0 0 1-.484.032l-.358-.325a.32.32 0 0 0-.484.032l-.378.48a.418.418 0 0 0 .036.54l1.32 1.267a.32.32 0 0 0 .484-.034l6.272-8.048a.366.366 0 0 0-.064-.512zm-4.1 0l-.478-.372a.365.365 0 0 0-.51.063L4.566 9.88a.32.32 0 0 1-.484.032L1.892 7.77a.366.366 0 0 0-.516.005l-.423.433a.364.364 0 0 0 .006.514l3.255 3.185a.32.32 0 0 0 .484-.033l6.272-8.048a.365.365 0 0 0-.063-.51z" fill="#4fc3f7" /></svg></span>
                <span className="time">3:30 PM</span>
            </div>
        )

    }

    // Fail Safe 
    return (
        <div className="message clearfix">
            <div className={props.message.source}>
                <div>{props.message.message}</div>
                <span className="tick"><svg xmlns="http://www.w3.org/2000/svg" width="16" height="15" id="msg-dblcheck-ack" x="2063" y="2076"><path d="M15.01 3.316l-.478-.372a.365.365 0 0 0-.51.063L8.666 9.88a.32.32 0 0 1-.484.032l-.358-.325a.32.32 0 0 0-.484.032l-.378.48a.418.418 0 0 0 .036.54l1.32 1.267a.32.32 0 0 0 .484-.034l6.272-8.048a.366.366 0 0 0-.064-.512zm-4.1 0l-.478-.372a.365.365 0 0 0-.51.063L4.566 9.88a.32.32 0 0 1-.484.032L1.892 7.77a.366.366 0 0 0-.516.005l-.423.433a.364.364 0 0 0 .006.514l3.255 3.185a.32.32 0 0 0 .484-.033l6.272-8.048a.365.365 0 0 0-.063-.51z" fill="#4fc3f7" /></svg></span>
                <span className="time">3:30 PM</span>
            </div>

        </div>
    )

}

// The bottom component, which includes Smiley Icon, "Type a message" field and Send button


function MessageCompose(props) {
    return (
        <div className="message-compose">
            <div className="input-field">
                <div className="smiley">
                    <i className="zmdi zmdi-mood"></i>
                </div>

                <div className="message-type">
                    <span>Type a message</span>
                </div>

                <div className="camera-icon">
                    <i className="zmdi zmdi-camera"></i>
                </div>
            </div>

            <div className="send-button" onClick={props.onSend}>
                <i className="zmdi zmdi-mail-send"></i>
            </div>

        </div>
    );
}



class ChatContainer extends Component {

    scrollToBottom = () => {
        this.messagesEnd.scrollIntoView({ behavior: "smooth" });
    }

    componentDidMount() {
        this.scrollToBottom();
    }

    componentDidUpdate() {
        this.scrollToBottom();
    }


    render() {

        return (
            <div className="chat-container">
                <div className="text-container">
                    {
                        this.props.messages.map((m, i) => {
                            return <Message key={i} message={m} />

                        })
                    }
                    <div style={{ float: "left", clear: "both" }}
                        ref={(el) => { this.messagesEnd = el; }}></div>
                </div>


                <MessageCompose />


            </div>
        );

    }

}

export default ChatContainer;
