import React, { Component } from 'react';
import './Whatsapp.css';
import Delayed from './Delayed';

// Structure of the Whatsapp Component

//     Whatsapp
//        ↳TopBar
//            ↳BackIcon
//            ↳Profile Picture
//            ↳Display Name
//            ↳More Setting Icon
//        ↳ChatContainer
//            ↳TextContainer
//                ↳Messages
//            ↳Message Compose
//                ↳Smiley Icon
//                ↳Type a message Field
//                ↳Camera Icon
//                ↳Send Button



// The main Component 

